pragma solidity ^0.5.0;

contract SocialNetwork {

    address private owner;
    uint public postCounter = 0;

    mapping (uint=>Post) public idToPost;

    event PostCreated(uint id,string content, uint tip, address payable author);
    event PostTipped(uint id,string content, uint tip, address payable author);


    constructor() public{
        owner = msg.sender;
    }

    struct Post{
        uint id;
        string content;
        uint tipAmount;
        address payable author;
        string videoUrl;
    }



    function createPost(string memory _content, string memory _url) public {
        require(bytes(_content).length > 0);
        postCounter++;
        idToPost[postCounter] = Post(postCounter,_content,0,msg.sender,_url);
        emit PostCreated(postCounter,_content,0,msg.sender);
    }

    function tipPost(uint _id) public payable{

        require(_id > 0 && _id <= postCounter);

        Post memory _post =  idToPost[_id];
        address payable _author = _post.author;
        _post.tipAmount = _post.tipAmount + msg.value;
        idToPost[_id] = _post;
        _author.transfer(msg.value);

        emit PostCreated(_post.id,_post.content,_post.tipAmount,_post.author);

    }

}
